const containerID = "18031f7e098a81df3eee505981ba395662d5142272cf0b3e2c22e70f32aa1ac4"
const containerName = "/test_cache_1"
const imageName = "postgres:9.5"
const imageNameLatestVersion = "postgres:latest"

const configHashLabel = "com.docker.compose.config-hash"
const configHashValue = "4bb26e2a02302e14145e88e7375f70f16c9f8a0a97e3999ceebe6949367c2b5e"
const containerNumberLabel = "com.docker.compose.container-number"
const containerNumberValue = "1"
const oneOffLabel = "com.docker.compose.oneoff"
const oneOffValue = "False"
const projectLabel = "com.docker.compose.project"
const projectValue = "test"
const serviceLabel = "com.docker.compose.service"
const serviceValue = "cache"
const versionLabel = "com.docker.compose.version"
const versionValue = "1.23.2"

func CreateTestData(t *testing.T, containerInfoJSON string, imageInfoJSON string) (types.ContainerJSON, types.ImageInspect) {
	var (
		container types.ContainerJSON
		image     types.ImageInspect
	)

	err := json.Unmarshal([]byte(containerInfoJSON), &container)
	assert.NoError(t, err)
	err = json.Unmarshal([]byte(imageInfoJSON), &image)
	assert.NoError(t, err)
	return container, image
}

func CreateTestContainer(t *testing.T, containerInfoJSON string, imageInfoJSON string) *core.Container {
	container, image := CreateTestData(t, containerInfoJSON, imageInfoJSON)
	c := core.NewContainer(&container, &image)
	assert.NotNil(t, c)
	return c
}

func CreateTestLabels() map[string]string {
	labels := make(map[string]string)
	labels[configHashLabel] = configHashValue
	labels[containerNumberLabel] = containerNumberValue
	labels[oneOffLabel] = oneOffValue
	labels[projectLabel] = projectValue
	labels[serviceLabel] = serviceValue
	labels[versionLabel] = versionValue
	return labels
}

func TestNewContainer(t *testing.T) {
	container, image := CreateTestData(t, containerInfo, imageInfo)

	assert.NotNil(t, core.NewContainer(nil, nil))
	assert.NotNil(t, core.NewContainer(&container, &image))
}

func TestID(t *testing.T) {
	c := CreateTestContainer(t, containerInfo, imageInfo)
	assert.Equal(t, containerID, c.ID())
}

func TestName(t *testing.T) {
	c := CreateTestContainer(t, containerInfo, imageInfo)
	assert.Equal(t, containerName, c.Name())
}

func TestImageName(t *testing.T) {
	c := CreateTestContainer(t, containerInfo, imageInfo)
	assert.Equal(t, imageName, c.ImageName())
	c1 := CreateTestContainer(t, contaienrInfoNoImageVerson, imageInfoNoVersion)
	a := c1.ImageName()
	log.Info(a)
	assert.Equal(t, imageNameLatestVersion, c1.ImageName())
}

func TestLabels(t *testing.T) {
	c := CreateTestContainer(t, containerInfo, imageInfo)
	testLabels := CreateTestLabels()
	assert.True(t, reflect.DeepEqual(testLabels, c.Labels()))
	assert.NotEmpty(t, c.Labels())
}

func TestGetProjectName(t *testing.T) {
	c := CreateTestContainer(t, containerInfo, imageInfo)
	projectName, err := c.GetProjectName()
	assert.Equal(t, projectName, projectValue)
	assert.NoError(t, err)
	c1 := CreateTestContainer(t, containerInfoNoLabels, imageInfo)
	projectName, err = c1.GetProjectName()
	assert.Error(t, err)
	assert.Empty(t, projectName)
}

func TestGetServiceName(t *testing.T) {
	c := CreateTestContainer(t, containerInfo, imageInfo)
	serviceName, err := c.GetServiceName()
	assert.Equal(t, serviceName, serviceValue)
	assert.NoError(t, err)
	c1 := CreateTestContainer(t, containerInfoNoLabels, imageInfo)
	serviceName, err = c1.GetServiceName()
	assert.Error(t, err)
	assert.Empty(t, serviceName)
}
